mapper.insert:
INSERT INTO purchase_in (
    io_id, purchase_no, supplier_name, product_name, product_code, quantity,
    unit_price, total_amount, purchase_type, status, purchase_date,
    create_time, update_time, remark, cid
) VALUES (
    #{ioId}, #{purchaseNo}, #{supplierName}, #{productName}, #{productCode},
    #{quantity}, #{unitPrice}, #{totalAmount}, #{purchaseType}, #{status},
    #{purchaseDate}, #{createTime}, #{updateTime}, #{remark}, #{cid}
)

mapper.select:
SELECT 
FROM purchase_in
WHERE io_id = #{ioId} AND cid = #{cid}
